<?php/** * @title * MagicBox: A Simple Version Control System * * @description * A Special Problem Presented to the Faculty of * The Institute of Computer Science * University of the Philippines Los Banos * * In Partial Fulfillment of the Requirements of the Degree of * Bachelor of Science in Computer Science * * @authors * Jasper A. Sibayan  * 2009-46112 * and  * Wilbert G. Verayin * 2009-60315 * @date * April 2013 */	function sqltoarray($result) {		$table_result=array();		$r=0;		while($row=mysql_fetch_assoc($result)) {			$arr_row=array();			$c=0;			while($c<mysql_num_fields($result)) {				$col=mysql_fetch_field($result,$c);				$arr_row[$col->name]=$row[$col->name];				$c++;			}			$table_result[$r]=$arr_row;			$r++;		}		mysql_free_result($result);		return $table_result;	}?>